import React, { useState, useEffect } from 'react'
import axios from 'axios'

import { Link } from 'react-router-dom'

function AllVocab(){
    const url = 'http://localhost/PWA-tutorial-training/learn-chinese/backend/index.php?lesson=flash';
    const [allVocab, setAllVocab] = useState(null);
    useEffect(() => {
        axios.get(url).then(response =>{
            setAllVocab(response.data)
        });
    }, [url]);

    const [showAnswer, setShowAnswer] = React.useState(false)
    const onClick = () => setShowAnswer(true)


    if(allVocab){
        return (
            <div>
                <Link to="/home/">
                    <button className="App-header">Home</button>
                </Link>
                {
                    allVocab.map(vocab => (
                        <div key={vocab.vid}>
                            <button className="LessonContent" onClick={onClick}>
                            {vocab.chinese}
                            </button>    
                        </div>
                    ))
                }
            </div>
        )
    }

    return (
        <div>
            No value
        </div>
    )
    const Answer = () => <p>
    Pinyin: {vocab.pinyin}
    <br></br>
    English: {vocab.english}
    </p>
}

export default AllVocab
