import React, { useState, useEffect } from 'react'
import axios from 'axios'

import { Link } from 'react-router-dom'

function AllVocab(){
    const url = 'http://localhost/PWA-tutorial-training/learn-chinese/backend/index.php?lesson=flash';
    const [allVocab, setAllVocab] = useState(null);
    useEffect(() => {
        axios.get(url).then(response =>{
            setAllVocab(response.data)
        });
    }, [url]);

    var entry = allVocab.map(function(row){
        return {
            key: row.vid,
            chinese: row.chinese,
            pinyin: row.pinyin,
            english: row.english
        }
    })

    if(allVocab){
        return (
            <div>
                <Link to="/home/">
                    <button className="App-header">Home</button>
                </Link>
                {
    allVocab.map(vocab => (
        <div key={vocab.vid}>
            <button className="LessonContent">
            {vocab.chinese}
                <p>
                    Pinyin: {vocab.pinyin}
                    <br></br>
                    English: {vocab.english}
                </p>
            </button>    
        </div>
    ))
}
            </div>
        )
    }

    return (
        <div>
            No value
        </div>
    )
}

export default AllVocab
