import React, { useState, useEffect } from 'react'
import axios from 'axios'

import { Link } from 'react-router-dom'

function AllVocab(){
    const [flip, setFlip] = useState(false)
    const url = 'http://localhost/PWA-tutorial-training/learn-chinese/backend/index.php?lesson=flash';
    const [allVocab, setAllVocab] = useState(null);
    useEffect(() => {
        axios.get(url).then(response =>{
            setAllVocab(response.data)
        });
    }, [url])

    if(allVocab){
        return (
            <div>
                <Link to="/home/">
                    <button className="App-header">Home</button>
                </Link>
                {
                    allVocab.map(vocab => (
                        <div key={vocab.vid}>
                            <button className="LessonContent" onClick={() => setFlip(!flip)}>
                            {vocab.chinese}
                            </button>
                            <button className="LessonContent">
                            Pinyin: {vocab.pinyin}
                            <br></br>
                            English: {vocab.english}
                            </button>


                        </div>
                    ))
                }
            </div>
        )
    }

    return (
        <div>
            No value
        </div>
    )
}

export default AllVocab
